import React, { useState } from 'react';
import '../../App.css';
import {BrowserRouter, Link, Route, Switch} from 'react-router-dom';

function TodoApp() {
    return (
        <div className="App">
            <BrowserRouter>
                <Switch>
                    <Route path="/" exact component={LoginComponent}/>
                    <Route path="/login" component={LoginComponent}/>
                    <Route path="/welcome/:name" component={WelcomeComponent}/>
                    <Route path="/todos" component={ListTodoComponent}/>
                    <Route component={ErrorComonent}/>
                </Switch>
            </BrowserRouter>
        </div>

    )
}

function ListTodoComponent(props){

    const [todos, setTodos] = useState([
        {
            id: 1,
            desc: "Learn React",
            done: false,
            date: new Date()
        },
        {
            id: 2,
            desc: "Learn java",
            done: false,
            date: new Date()
        },
        {
            id: 3,
            desc: "Learn springboot",
            done: false,
            date: new Date()
        }
    ])

    return (
        <div>
            <h1>List Todo</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Done</th>
                        <th>Date</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        todos.map((todo,index) => 
                        <tr key={"todo"+index}>
                            <td>{todo.id}</td>
                            <td>{todo.desc}</td>
                            <td>{todo.done}</td>
                            <td>{todo.date}</td>
                        </tr>    
                        )
                    }
                </tbody>
            </table>
        </div>
    );
}

function WelcomeComponent(props){
    return (
        <div>
            <p>Welcome to {props.match.params.name}'s world</p>
            <p>You can manage your todos <Link to="/todos">here</Link></p>
        </div>
    );
}

function ErrorComonent(){
    return(
        <div>An Error Occurred.</div>
    )
}

function LoginComponent(props) {

    const [userName, setUserName] = useState("jackyhaha");
    const [passWord, setPassWord] = useState("");
    const [hasLogin, setHasLogin] = useState();

    const handleUserName = (e) => {
        setUserName(e.target.value);
        console.log(e.target.name);
        console.log(e.target.value);
    }

    const handlePassWord = (e) => {
        setPassWord(e.target.value);
        console.log(e.target.value);
    }

    const loginClicked = () => {
        // jacky, 123
        if(userName === "jacky" && passWord === "123"){
            props.history.push(`/welcome/${userName}`);
        } else{
            setHasLogin(0);
        }
    }

    return (
        <div>
            <ShowLoginMessage hasLogin={hasLogin}/>
            User Name: <input type="text" name="username" value={userName} onChange={(e) =>handleUserName(e)}></input><br/>
            Password: <input type="password" name="password" value={passWord} onChange={(e) => handlePassWord(e)}></input><br/>
            <button onClick={loginClicked}>Login</button>
        </div>
    )


}

function ShowLoginMessage(props){
    if(props.hasLogin === 1){
        return(
            <div>Successful</div>
        )
    } else if(props.hasLogin === 0){
        return (
            <div>Invaild</div>
        );
    }
    return null;
}

export default TodoApp;
