import React, { useState } from 'react';
import '../../App.css';
import {BrowserRouter, Link, Route, Switch} from 'react-router-dom';
import AuthenticationService from './AuthenticationService.js';

function TodoApp() {
    const [isUserLogIn, setIsUserLogIn] = useState(AuthenticationService.isUserLogin());
    console.log(isUserLogIn);
    const handleLogout = () => {
        setIsUserLogIn(false);
    }

    return (
        <div className="App">
            <BrowserRouter>
                <HeaderComponent isUserLogIn={isUserLogIn} onChange={handleLogout}/>
                <Switch>
                    <Route path="/" exact component={LoginComponent}/>
                    <Route path="/login" component={LoginComponent}/>
                    <Route path="/welcome/:name" component={WelcomeComponent}/>
                    <Route path="/todos" component={ListTodoComponent}/>
                    <Route path="/logout" component={LogoutComponent}/>
                    <Route component={ErrorComonent}/>
                </Switch>
                <FooterComponent/>
            </BrowserRouter>
        </div>

    )
}

function HeaderComponent(props){

    const handleLogout = () => {
        props.onChange();
    }

    return(
        <header>
            <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                <div><a href="https://www.google.com/?hl=zh_tw" className="navbar-brand">jacky</a></div>
                <ul className="navbar-nav">
                    {props.isUserLogIn && <li><Link className="nav-link" to="/welcome/jacky">Home</Link></li>}
                    {props.isUserLogIn && <li><Link className="nav-link" to="/todos">Todos</Link></li>}
                </ul>
                <ul className="navbar-nav navbar-collapse justify-content-end">
                    {!props.isUserLogIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
                    {props.isUserLogIn && <li><Link className="nav-link" to="/logout" onClick={() => {AuthenticationService.logout(); handleLogout();}}>Logout</Link></li>}
                </ul>
            </nav>
        </header>
    )
}

function LogoutComponent(){
    return(
        <div>
            <h1>You are logout</h1>
            <div className="container">
                Thank you for using our application.
            </div>
        </div>
    )
}

function FooterComponent(){
    return(
        <footer className="footer">
            <span className="text-muted">All Rights Reserved 2021 @jacky</span>
        </footer>
    )
}

function ListTodoComponent(props){

    const [todos, setTodos] = useState([
        {
            id: 1,
            desc: "Learn React",
            done: false,
            date: new Date()
        },
        {
            id: 2,
            desc: "Learn java",
            done: false,
            date: new Date()
        },
        {
            id: 3,
            desc: "Learn springboot",
            done: false,
            date: new Date()
        }
    ])

    return (
        <div>
            <h1>List Todo</h1>
            <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Description</th>
                            <th>Done</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            todos.map((todo,index) => 
                            <tr key={"todo"+index}>
                                <td>{todo.id}</td>
                                <td>{todo.desc}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.date.toString()}</td>
                            </tr>    
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

function WelcomeComponent(props){
    return (
        <div>
            <h1>Welcome!</h1>
            <div className="container">
                <p>Welcome to {props.match.params.name}'s world</p>
                <p>You can manage your todos <Link to="/todos">here</Link></p>
            </div>
        </div>
    );
}

function ErrorComonent(){
    return(
        <div>An Error Occurred.</div>
    )
}

function LoginComponent(props) {

    const [userName, setUserName] = useState("jackyhaha");
    const [passWord, setPassWord] = useState("");
    const [hasLogin, setHasLogin] = useState();

    const handleUserName = (e) => {
        setUserName(e.target.value);
        console.log(e.target.name);
        console.log(e.target.value);
    }

    const handlePassWord = (e) => {
        setPassWord(e.target.value);
        console.log(e.target.value);
    }

    const loginClicked = () => {
        // jacky, 123
        if(userName === "jacky" && passWord === "123"){
            AuthenticationService.registerSuccessfulLogin(userName, passWord);
            props.history.push(`/welcome/${userName}`);
        } else{
            setHasLogin(0);
        }
    }

    return (
        <div>
            <h1>Login</h1>
            <div className="container">
                <ShowLoginMessage hasLogin={hasLogin}/>
                User Name: <input type="text" name="username" value={userName} onChange={(e) =>handleUserName(e)}></input><br/>
                Password: <input type="password" name="password" value={passWord} onChange={(e) => handlePassWord(e)}></input><br/>
                <button className="btn" onClick={loginClicked}>Login</button>
            </div>
        </div>
    )


}

function ShowLoginMessage(props){
    if(props.hasLogin === 1){
        return(
            <div >Successful</div>
        )
    } else if(props.hasLogin === 0){
        return (
            <div className="alert alert-warning">Invaild</div>
        );
    }
    return null;
}

export default TodoApp;
