import React, { useEffect, useState } from 'react';
import TodoDataService from '../../api/todo/TodoDataService.js';
import AuthenticationService from './AuthenticationService.js';

function ListTodoComponent(){

    const [todos, setTodos] = useState([]); 
    const [deleteMessage, setDeleteMessage] = useState();
    useEffect(() => {
        const username = AuthenticationService.getLoginUserName();
        TodoDataService.retrieveAllTodos(username).then(res => setTodos(res.data));
    },[])

    const deleteTodoClick = (id) => {
        const username = AuthenticationService.getLoginUserName();
        TodoDataService.deleteTodo(username, id).then(res => setDeleteMessage(`delete ${id} successfully`));
    }

    return (
        <div>
            <h1>List Todo</h1>
            <div className="alert alert-success">{deleteMessage}</div>
            <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Description</th>
                            <th>Done</th>
                            <th>Date</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            todos.map((todo,index) => 
                            <tr key={"todo"+index}>
                                <td>{todo.id}</td>
                                <td>{todo.description}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.targetDate.toString()}</td>
                                <td><button className="btn btn-danger" onClick={()=>deleteTodoClick(todo.id)} >delete</button></td>
                            </tr>    
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ListTodoComponent;
